tinymce.PluginManager.add('hotimg', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('hotimg', {
        text: 'Media Library',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Media Library Plugin',
                url: '/hotcms/media-library/tinymce_image_picker',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    //console.log(e);
                    //editor.insertContent(document.forms[0].imagesrc.value);
                },
                onclose: function(e){
                  //editor.insertContent(document.forms[0].imagesrc.value);                
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('hotimg', {
        text: 'Media Library',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: 'http://www.tinymce.com',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]            
            });
        }
    });
});