                          <p>Fedex #: <?php echo $order->fedex_number ?></p>                            </div>                        </div>                    </div>                    <div id="receipt-address">                        <strong>Bill to:</strong>                        <p><?php echo $order->billing_firstname . ' ' . $order->billing_lastname; ?></p>                        <p><?php echo $order->billing_street1 ?></p>                        <?php if (!empty($order->billing_street2)) { ?>                            <p><?php echo $order->billing_street2 ?></p>                        <?php } ?>                        <p><?php echo $order->billing_city . ' ' . $order->billing_province; ?></p>                        <p><?php echo $order->billing_postal; ?></p>                    </div>                </div>            <?php } ?>        </div>    </body></html>