<div id="shopping-cart" class="container-fluid hero-unit">  <div id="cart-title">    <h1><?php echo $oModule->title; ?></h1>  </div>  <div id="messageContainer">    <?php    if (isset($messages) && is_array($messages)) {      foreach ($messages as $msg) {        if (is_array($msg) && $msg['message'] > '') {          echo '<div class="message ' . $msg['type'] . '">';          echo '<div class="message_close"><a onClick="closeMessage()">[close]</a></div>';          echo $msg['message'] . '</div>';        }      }    }    ?>  </div>  <?php if (!is_null($cart)) { ?>    <div id="cart">      <!-- begin cart div -->      <?php if (count($cart) > 0) { ?>        <!-- begin cart has items -->        <div id="cart-header">          <div class="heading heading-left column1">Product</div>          <div class="heading column2 ">Quantity</div>          <div class="heading column3 ">Points</div>          <div class="heading column4">Item total</div>          <div class="heading heading-right column5">Remove</div>        </div>        <?php foreach ($cart as $item) { ?>          <div class="cart-item">            <?php echo form_open('http://' . $_SERVER['HTTP_HOST'] . '/shop/my-cart'); ?>            <?php echo form_hidden('rowid', $item['rowid']); ?>            <?php echo form_hidden('id', $item['id']); ?>            <div class="field column1">              <div class="item-name">                <?php              printf('<div style="margin-right:10px; width: 60px; float: left;"><img src="%s" alt="Product Image" width="50" height="100" /></div>',$item['featured_image']->full_path);              echo $item['name'];            ?></div>            </div>            <div class="field column2">      <?php echo $item['qty'] ?>              <!--select name="quantity" id="quantity">                <option value="0"> &nbsp; 0 &nbsp; </option>              <?php              for ($i = 1; $i < 21; $i++) {                echo '<option value="' . $i . '" ' . ($i == $item['qty'] ? 'selected="selected"' : '') . '> &nbsp; ' . $i . ' &nbsp; </option>' . "\n";              }              ?>                 </select>-->            </div>            <div class="field column3"><?php echo number_format($item['price'], 0, '.', ','); ?></div>            <div class="field column4">              <div class="total"><?php echo number_format($item['subtotal'], 0, '.', ','); ?></div>            </div>            <div class="field column5">              <div class="cart-remove-wrapper">                <div class="cart-remove-icon"><?php echo anchor('http://' . $_SERVER['HTTP_HOST'] . '/shop/remove/' . $item['rowid'], '<img alt="remove" src="/themes/cheddarLabs/images/icons/remove.jpg"/>'); ?></div>              </div>            </div>                  <?php echo form_close(); ?>          </div>    <?php } ?>        <div class="cart-total">          <div class="cart-total-label total column1 field">TOTAL:</div>          <div class="cart-total-label total column2 field"></div>          <div class="cart-total-label total column3 field"></div>          <div class="cart-total-label total column4 field"></div>          <div class="cart-total total column5 field align-center"><?php echo number_format($this->cart->total(), 0, '.', ','); ?></div>        </div>        <div style="clear: both"></div>        <br />        <div id="cart-continue">          <?php if(has_permission('spent_points')){?>          <a href="/shop/shipping" class="btn btn-primary btn-large">Proceed to shipping</a>          <?php } else {?>              <a href="#shopCartModal" data-toggle="modal" role="button" class="btn btn-primary btn-large">Proceed to shipping</a>          <?php }?>          <a href="/shop/empty_cart" class="btn btn-primary btn-large">Empty cart</a>          <?php //echo anchor('https://' . $_SERVER['HTTP_HOST'] . '/shop/shipping', 'CHECKOUT', 'class="aTriangleButton"'); ?>    <?php //echo anchor('https://' . $_SERVER['HTTP_HOST'] . '/shop/cart', 'UPDATE', 'class="aTriangleButton updateCart"');  ?>          <!-- a id="cart-keep-shopping" class="aTriangleButton" href="/products">KEEP SHOPPING</a -->        </div>        <!-- end cart has items -->  <?php } else { ?>        <!-- no items -->        <div id="cart-header">          <div class="heading heading-left column1">Product</div>          <div class="heading column2 ">Quantity</div>          <div class="heading column3 ">Points</div>          <div class="heading column4">Item total</div>          <div class="heading heading-right column5">Remove</div>        </div>        <div class="cart-item">             <div id="cart-empty" class="item-name">Your virtual shopping cart is virtually empty. <br/>Please put something in it before checking out. </div>        </div>        <div style="clear: both"></div>        <a  class="btn btn-primary btn-large" id="cart-keep-shopping" href="/shop">KEEP SHOPPING</a>  <?php } ?>      <!-- end of cart div -->    </div><?php } ?></div><!-- Modal --><div id="shopCartModal" class="modal hide fade" tabindex="-1" role="dialog" aria-labelledby="shopCartModalLabel" aria-hidden="true"><div class="modal-header"><button type="button" class="close" data-dismiss="modal" aria-hidden="true">Ã—</button><h3 id="myModalLabel">Checkout unavailable</h3></div><div class="modal-body"><p>Sorry, your retailer hasn't enabled orders yet. Please check back soon!</p></div><div class="modal-footer"><button class="btn" data-dismiss="modal" aria-hidden="true">Close</button></div></div>