<div id="shopping-confirm" class="container-fluid hero-unit">    <h2><?php echo $sTitle; ?></h2>    <div id="messageContainer">        <?php        if (isset($messages) && is_array($messages)) {            foreach ($messages as $msg) {                if (is_array($msg) && $msg['message'] > '') {                    echo '<div class="message ' . $msg['type'] . '">';                    echo '<div class="message_close"><a onClick="closeMessage()">[close]</a></div>';                    echo $msg['message'] . '</div>';                }            }        }        ?>    </div>    <div class="row-fluid">        <div class="span4">            <?php if (isset($success) && $success == '1') {                ?>                <h3>Your purchase is complete.</h3>                <h3>Your order has been placed!</h3>                <p>Print this page to save a copy of your order details.</p>                <?php            }            ?>            <h3>Ship to:</h3>            <p><?php echo $order->shipping_firstname . ' ' . $order->shipping_lastname; ?></p>            <p><?php echo $order->shipping_street1 . ' ' . $order->shipping_street2; ?></p>                         <p><?php echo $order->shipping_city . ',' . $order->shipping_province; ?></p>                   <p><?php echo $order->shipping_postal ?></p>                   <p>Order Date/Time: <?php echo date('Y-m-d h:i:s a', $order->create_timestamp); ?></p>            <p><a class="btn btn-primary btn-large" href="/shop/receipt" target="_parent">PRINT RECEIPT</a></p>        </div>        <div class="span8">            <?php print $cart_review ?>        </div>    </div>