<div id="shopping-payment" class="container-fluid hero-unit"><h1><?php echo $sTitle;?></h1><?php if ( $message>'' ){ ?><div class="message"><?php echo $message;?></div><?php } ?><?php if ( $error>'' ){ ?><div class="error"><?php echo $error;?></div><?php } ?><?php if (!is_null($order)) { ?><div id="order"><?php if (count($order->items) > 0) { ?>  <div id="cart-summary-wrapper" class="cart-summary-panel">  <div id="cart-summary-wrapper-left">    <div class="cart-header">      <p class="title">Order Summary</title>    </div>    <div id="cart-summary">    <?php foreach ($order->items as $item) { ?>      <div class="cart-summary-item">	<div class="cart-summary-item-wrapper">	<div class="cart-summary-item-description">	<?php	  if ((int)$item->qty > 1) {	    echo $item->qty . " X ";	  }	  echo $item->title;	  if ($item->option > '') {	    echo ' &ndash; ' . format_phone_number($item->option);	  }	?></div>	<div class="cart-summary-item-total">$<?php echo number_format($item->price, 2, '.', ','); ?></div>      </div>      </div>    <?php } ?>    <div class="cart-summary-tax-item">    <div class="cart-summary-tax-wrapper">      <div class="cart-summary-total-subtotal">$<?php echo number_format($order->subtotal, 2, '.', ','); ?></div>      <div class="cart-summary-total-label">Subtotal:</div>    </div>    </div>    <div class="cart-summary-tax-item">    <div class="cart-summary-tax-wrapper cart-summary-tax-last">      <div class="cart-summary-total-subtotal">$<?php echo number_format($order->tax, 2, '.', ','); ?></div>      <div class="cart-summary-total-label">Tax (<?php echo $order->tax_name; ?>)</div>    </div>    </div>    <div class="cart-summary-total">      <div class="cart-summary-total-subtotal">$<?php echo number_format($order->order_total, 2, '.', ','); ?></div>      <div class="cart-summary-total-label">Order Total:</div>    </div>    </div>  </div>  <div id="cart-summary-wrapper-right">    <p class="title">Billing Information</p>    <div id="billing-summary">      <div id="billing-name"><span class="label"><?php echo $order->billing_firstname." ".$order->billing_lastname; ?></span></div>      <div id="billing-street1"><?php echo $order->billing_street1; ?></div>      <?php if(!empty($order->billing_street1)) { ?>	<div id="billing-street2"><?php echo $order->billing_street2; ?></div>      <?php } ?>      <div id="billing-city-province-postal"><?php echo $order->billing_city; ?>, <?php echo $order->billing_province; ?> <?php echo $order->billing_postal; ?></div>      <div id="billing-email">	<span class="label">Email:</span>	<?php echo $order->billing_email; ?>      </div>      <div id="billing-phone">	<span class="label">Tel:</span>	<?php echo format_phone_number($order->billing_phone); ?>      </div>    </div>  </div>  <a href="/shop/checkout-billing" class="cart-return-link">Return to previous page <img width="8" height="8" alt="Return to previous page" src="/asset/images/link_arrow.png" /></a>  <div id="gateway-wrapper">    <iframe src="<?php echo $iframe_url; ?>" width="928" height="400" frameborder="0" id="gateway" name="gateway" ><p>Your browser does not support iframes.</p></iframe>  </div><?php } ?></div></div><?php } ?></div>