<div id="shopping-receipt" class="container-fluid hero-unit"><div id="receipt-preamble">  <h1>Receipt</h1></div><?php if (!is_null($order)) { ?><div id="receipt"><?php if (count($order->items) > 0) { ?>  <div id="cart-summary-wrapper" class="cart-summary-panel-print">    <div class="cart-header">      <p class="title">Order Summary</title>    </div>    <div id="cart-summary">      <?php foreach ($order->items as $item) { ?>      <div class="cart-summary-item-print">        <div class="cart-summary-item-wrapper">          <div class="cart-summary-item-description-print">          <?php          if ((int)$item->qty > 1) {            echo $item->qty . " X ";          }          echo $item->product_name;          if ($item->option_title > '' && $item->option_value > '') {            echo ' &ndash; ' . $item->option_title . ' ' . $item->option_value;          }          ?>          </div>          <div class="cart-summary-item-total">        <?php echo number_format($item->price, 0, '.', ','); ?> POINTS          </div>        </div>      </div>      <?php } ?>      <div class="cart-summary-tax-item-print">        <div class="cart-summary-tax-wrapper">          <div class="cart-summary-total-subtotal"><?php echo number_format($order->subtotal, 0, '.', ','); ?> POINTS</div>          <div class="cart-summary-total-label">Subtotal</div>        </div>      </div>      <div class="cart-summary-total-print">        <div class="cart-summary-total-subtotal"><?php echo number_format($order->order_total, 0, '.', ','); ?> POINTS</div>        <div class="cart-summary-total-label">Order Total</div>      </div>  <?php } ?>    </div>  </div>  <div id="receipt-detail">    <strong>Transaction Details:</strong>    <div id="receipt-basic">      <div id="receipt-basic-right">        <p><?php echo date('Y-m-d h:i:s a', $order->create_timestamp); ?></p>        <p>Order #:<?php echo $order->id ?></p>      </div>    </div>  </div>  <div id="receipt-address">    <strong>Bill to:</strong>    <p><?php echo $order->billing_firstname . ' ' . $order->billing_lastname; ?></p>    <p><?php echo $order->billing_street1 ?></p>    <?php if(!empty($order->billing_street2)) { ?>      <p><?php echo $order->billing_street2 ?></p>    <?php } ?>    <p><?php echo $order->billing_city . ' ' . $order->billing_province; ?></p>    <p><?php echo $order->billing_postal; ?></p>  </div></div><?php } ?></div>