<div id="shopping-cart-review" class="container-fluid hero-unit">  <div id="messageContainer">    <?php    if (isset($messages) && is_array($messages)) {      foreach ($messages as $msg) {        if (is_array($msg) && $msg['message'] > '') {          echo '<div class="message ' . $msg['type'] . '">';          echo '<div class="message_close"><a onClick="closeMessage()">[close]</a></div>';          echo $msg['message'] . '</div>';        }      }    }    ?>  </div>      <div id="cart" class="review">      <!-- begin cart div -->      <?php if (count($cart) > 0) { ?>        <!-- begin cart has items -->        <div id="cart-header">          <div class="heading heading-left column1">Product</div>          <div class="heading column2 ">Quantity</div>          <div class="heading column3 ">Points</div>          <div class="heading column4">Item total</div>        </div>        <?php         foreach ($cart->items as $item) {                         ?>          <div class="cart-item">            <div class="field column1">              <div class="item-name">                <?php              printf('<div class="pull-left">%s</div>',$item->featured_image);              print('<div class="pull-left product-name">'.$item->product_name.'</div>');            ?></div>            </div>            <div class="field column2">             <?php echo $item->qty ?>            </div>            <div class="field column3"><?php echo number_format($item->price, 0, '.', ','); ?></div>            <div class="field column4">              <div class="total"><?php echo number_format($item->price*$item->qty, 0, '.', ','); ?></div>            </div>               </div>    <?php } ?>        <div class="cart-total">          <div class="cart-total-label total column1 field">TOTAL:</div>          <div class="cart-total-label total column2 field"></div>          <div class="cart-total-label total column3 field" colspan="2"></div>          <div class="cart-total total column5 field align-center"><?php echo number_format($cart->order_total, 0, '.', ','); ?> POINTS</div>                  </div>        <div style="clear: both"></div>        <br />        <!-- end cart has items -->      <!-- end of cart div -->    </div><?php } ?></div>