<div id="shopping-shipping" class="container-fluid hero-unit">  <?php if ($sTitle > '') { ?><h1><?php echo $sTitle; ?></h1><?php } ?>  <div class="">    <h2>MANAGE YOUR ADDRESS BOOK</h2>    <div id="messageContainer">      <?php      if (isset($messages) && is_array($messages)) {        foreach ($messages as $msg) {          if (is_array($msg) && $msg['message'] > '') {            echo '<div class="message ' . $msg['type'] . '">';            echo $msg['message'] . '</div>';          }        }      }      ?>    </div>    <div id="cart-shipping">        <div class="row-fluid">            <div class="span6">                <div class="row-fluid">                    <a id="add_new_contact" class="btn btn-primary btn-large">Add new address</a>                </div>                <br />                      <form id="form_shipping" name="form_shipping" method="post">        <div class="form-row">          <ul class="addresses">            <?php            foreach ($addresses as $addr) {              echo '<li class="row-fluid">';              echo '<div class="span1">';              echo form_radio('address', $addr->id, ($addr->default==1)?TRUE:FALSE);              echo '</div>';              echo '<div class="span4">';              echo '<strong>' . ucfirst($addr->name) . '</strong><br />';              echo $addr->address_1 . '<br />';              if ($addr->address_2 > '') {                echo $addr->address_2 . '<br />';              }              echo $addr->city . '<br />';              echo $addr->province . '<br />';              echo $addr->postal_code . '<br />';              echo '</div>';              echo '</li>';              //TODO: Add/Edit Address button and Ajax forms using Contact helper            }            ?>          </ul>        </div>        <div class="row-fluid">          <div class="span6">            <label for="phone">Phone Number:<span class="red">*</span></label>            <?php echo form_input($phone); ?>          </div>                     <div class="span6 mandatory-fields-wrappper">            All fields marked with <span class="red">*</span> are mandatory.          </div>                      </div>        <div class="row-fluid">          <div class="span12">            <label for="instructions">Sizing, product specifications, and/or shipping notes:</label>            <?php echo form_textarea($instruction); ?>          </div>                   </div>        <div class="row-fluid">            <a href="#" class="btn btn-primary btn-large" onclick="document.forms['form_shipping'].submit(); return false;">CONFIRM ORDER</a>        </div>      </form>    </div>            <div class="span4">        <div class="row-fluid">            <div id="new_contact_form_wrapper" class="hidden" style="display:none;">            <?php            print($contact_form)            ?>            </div>        </div>                      </div></div>